var lists=[{author:"author1",name:"name1",src:"mp3/1.mp3",cover:"img/1.jpg"},{author:"author2",name:"name2",src:"mp3/2.mp3",cover:"img/2.jpg"},{author:"author3",name:"name3",src:"mp3/3.mp3",cover:"img/3.jpg"},{author:"author4",name:"name4",src:"mp3/4.mp3",cover:"img/4.jpg"}];var listLength=lists.length;var music=$('#music')[0];var musicNow=0;var musicMode=0;music.volume=0.8;function init(){for(var i=0;i<listLength;i++){var tr='<tr class="row"><td class="list-name">'+lists[i].name+'</td><td class="list-author">'+lists[i].author+'</td></tr>';$('table').append(tr)}}function togglePlay(){if(music.paused){$('#music-play').removeClass('glyphicon-play');$('#music-play').addClass('glyphicon-pause');music.play();$('.album-cover').removeClass('animation-paused')}else{$('#music-play').addClass('glyphicon-play');$('#music-play').removeClass('glyphicon-pause');music.pause();$('.album-cover').addClass('animation-paused')}}function preMusic(){switch(musicMode){case 0:musicNow--;if(musicNow<0){musicNow=listLength-1}break;case 1:break;case 2:musicNow=parseInt(listLength*Math.random());break}$('#music-play').removeClass('glyphicon-play');$('#music-play').addClass('glyphicon-pause');freshInformation();$('.album-cover').removeClass('animation-paused')}function nextMusic(){switch(musicMode){case 0:musicNow++;if(musicNow>(listLength-1)){musicNow=0}break;case 1:break;case 2:musicNow=parseInt(listLength*Math.random());break}$('#music-play').removeClass('glyphicon-play');$('#music-play').addClass('glyphicon-pause');freshInformation();$('.album-cover').removeClass('animation-paused')}function stopMusic(){music.load();$('#music-play').addClass('glyphicon-play');$('#music-play').removeClass('glyphicon-pause');music.pause();$('.album-cover').addClass('animation-paused')}function progressJump(event){var i=event.offsetX/470;music.currentTime=i*music.duration}function volumeJump(event){music.muted=false;var i=event.offsetX/50;var percentI=toPercent(i);music.volume=i;freshVolume();$('.volume-progress-bar').css({width:percentI})}function freshVolume(){if(music.volume<=1&&music.volume>0.5){$('.volume-icon').removeClass('glyphicon-volume-off');$('.volume-icon').removeClass('glyphicon-volume-down');$('.volume-icon').addClass('glyphicon-volume-up')}else if(music.volume>0&&music.volume<=0.5){$('.volume-icon').removeClass('glyphicon-volume-off');$('.volume-icon').addClass('glyphicon-volume-down');$('.volume-icon').removeClass('glyphicon-volume-up')}else if(music.volume===0){$('.volume-icon').addClass('glyphicon-volume-off');$('.volume-icon').removeClass('glyphicon-volume-down');$('.volume-icon').removeClass('glyphicon-volume-up')}}function freshInformation(){$('.author').text(lists[musicNow].author);$('.name').text(lists[musicNow].name);$('.album-cover').attr("src",lists[musicNow].cover);$('#music').attr('src',lists[musicNow].src)}function toPercent(data){var strData=parseFloat(data)*100;var ret=strData.toString()+"%";return ret}function freshProgress(){var i=toPercent(music.currentTime/music.duration);$('.progress-bar').css({width:i});if(music.ended){nextMusic()}}function changeMode(){musicMode++;if(musicMode>2){musicMode=0}else if(musicMode<0){musicMode=2}switch(musicMode){case 0:$('.music-mode').addClass('glyphicon-refresh');$('.music-mode').removeClass('glyphicon-repeat');$('.music-mode').removeClass('glyphicon-random');break;case 1:$('.music-mode').removeClass('glyphicon-refresh');$('.music-mode').addClass('glyphicon-repeat');$('.music-mode').removeClass('glyphicon-random');break;case 2:$('.music-mode').removeClass('glyphicon-refresh');$('.music-mode').removeClass('glyphicon-repeat');$('.music-mode').addClass('glyphicon-random');break}}function volumeMuted(){if(music.muted){music.muted=false;freshVolume();var temp=toPercent(music.volume);$('.volume-progress-bar').css({width:temp})}else{music.muted=true;$('.volume-icon').addClass('glyphicon-volume-off');$('.volume-icon').removeClass('glyphicon-volume-down');$('.volume-icon').removeClass('glyphicon-volume-up');$('.volume-progress-bar').css({width:0})}}$(document).ready(function(){switch(musicMode){case 0:$('.music-mode').addClass('glyphicon-refresh');break;case 1:$('.music-mode').addClass('glyphicon-repeat');break;case 2:$('.music-mode').addClass('glyphicon-random');break}$('.volume-progress-bar').css({width:toPercent(music.volume)});freshInformation();freshVolume();setInterval(freshProgress,100);init();$('#music-play').click(togglePlay);$('.glyphicon-fast-backward').click(preMusic);$('.glyphicon-fast-forward').click(nextMusic);$('.glyphicon-stop').click(stopMusic);$('.progress').mousedown(progressJump);$('.volume-progress').mousedown(volumeJump);$('.music-mode').click(changeMode);$('.volume-icon').click(volumeMuted);$('.music-list').mouseover(function(){console.log("mouseover");$('.music-list-dialog').fadeIn()});$('.music-list').mouseout(function(){$('.music-list-dialog').fadeOut()})});